let lighting    = global.get('lighting')
let settings    = lighting.settings
let times       = lighting.times
let switches    = global.get('switches')
let sun         = switches.sun_position
let sun_percent = msg.payload.altitudePercent/100
let dateNow     = Date.now()

if (sun == 'day'){
    colorTemperature = (settings.set_max-settings.set_min) * sun_percent + settings.set_min
    brightness = settings.brightness_max
} else if (sun == 'night'){
    colorTemperature = settings.set_min
    brightness = settings.brightness_min
} else {
    if(sun == 'dawn'){
        periodDuration = times.sunrise - times.dawn
        periodActual = dateNow - times.dawn
    } else if (sun == 'dusk') {
        periodDuration = times.dusk - times.sunset
        periodActual = times.dusk - dateNow
    } else {
        node.error({payload: 'problem in Calc', sun : praktijk.sun})
    }
    brightness = (settings.brightness_max - settings.brightness_min) * periodActual/periodDuration + settings.brightness_min
    colorTemperature = settings.set_min
}
colorTemperature = 100 - (100 * (colorTemperature - settings.bulb_min)/(settings.bulb_max - settings.bulb_min))

newMsg = {
