let voorloop = 30
let uitloop = 60
let praktijk = global.get("praktijk") || {}
let blokken_agenda = []
let blok_inkom = {}

// get calendar events, take out all-day events
events = msg.payload.filter(function(event) {
    return event.allDay !== true
})

if (events.length > 0){
    // take out irrelevant properties
    events = events.map(({eventStart, eventEnd}) => ({eventStart, eventEnd}))
    // insert voorloop and uitloop
    events.forEach(function(item, index) {
        events[index].eventStart.setMinutes(item.eventStart.getMinutes() - voorloop)
        events[index].eventEnd.setMinutes(item.eventEnd.getMinutes() + uitloop)
    });
    blok_inkom.start = events[0].eventStart
    blok_inkom.end   = events[events.length - 1].eventEnd

    eventStart       = events[0].eventStart
    eventEnd         = events[0].eventEnd
    events.forEach(function(event) {
        if (event.eventStart > eventEnd) {
            blokken_agenda.push({start: eventStart, end: eventEnd})
            eventStart = event.eventStart
        }
        eventEnd = event.eventEnd
    });
    blokken_agenda.push({start: eventStart, end: eventEnd})

    // take out expired events
    now = new Date()
    blokken_agenda = blokken_agenda.filter(function(blok){
        return blok.end > now
    })
}

praktijk.blokken_agenda = blokken_agenda
praktijk.blok_inkom = blok_inkom
